mapper.select:
select dongCode, gugunName
from dongcode
where dongName is null and gugunName is not null and sidoName = #{sido}
order by dongcode;

select i.* 
from houseinfo i
inner join dongcode d
on d.dongCode = i.dongCode
where d.sidoName = #{sidoName} and d.gugunName = #{gugunName}
and d.dongName=#{dongName}
and i.apartmentName like concat('%',#{aptName},'%')
limit #{start}, #{spp}

select
(case when (select count(*) from likeapt where aptCode =#{aptCode} and userid=#{userid}) = 1 then 'Y' else 'N' end) 'check',
i.* from houseinfo i where aptCode = '11110000000001';
